#!/bin/bash
set -e

cd Dependencies/aom

rm config/*.h

mkdir __build
cd __build
$CMAKE_COMMAND .. -DCMAKE_INSTALL_PREFIX=$INSTALL_PREFIX -DBUILD_SHARED_LIBS=false -DCMAKE_BUILD_TYPE=$CMAKE_BUILD_TYPE -DAOM_TARGET_CPU=generic -DCONFIG_RUNTIME_CPU_DETECT=false -DCONFIG_MULTITHREAD=false -DENABLE_DOCS=false -DENABLE_EXAMPLES=false -DENABLE_TESTS=false -DENABLE_TOOLS=false -DCONFIG_WEBM_IO=false -DCMAKE_C_FLAGS="$FLAGS"
$MAKE install
