#!/bin/bash
set -e

cd Dependencies/de265

mkdir __build
cd __build
$CMAKE_COMMAND .. $CMAKE_OPTIONS -DCMAKE_INSTALL_PREFIX=$INSTALL_PREFIX -DCMAKE_FIND_ROOT_PATH=$INSTALL_PREFIX -DBUILD_SHARED_LIBS=false  -DENABLE_DECODER=off -DCMAKE_BUILD_TYPE=$CMAKE_BUILD_TYPE -DCMAKE_C_FLAGS="$FLAGS" -DCMAKE_CXX_FLAGS="$FLAGS"
$MAKE install
