#!/bin/bash
set -e

cd Dependencies/xml

rm -Rf config.h
rm -Rf include/libxml/xmlversion.h

mkdir __build
cd __build
$CMAKE_COMMAND .. -DCMAKE_INSTALL_PREFIX=$INSTALL_PREFIX -DBUILD_SHARED_LIBS=false -DCMAKE_BUILD_TYPE=$CMAKE_BUILD_TYPE -DCMAKE_C_FLAGS="$FLAGS" -DLIBXML2_WITH_C14N=false -DLIBXML2_WITH_CATALOG=false -DLIBXML2_WITH_DEBUG=false -DLIBXML2_WITH_HTML=false -DLIBXML2_WITH_HTTP=false -DLIBXML2_WITH_ICONV=false -DLIBXML2_WITH_LZMA=false -DLIBXML2_WITH_OUTPUT=false -DLIBXML2_WITH_PATTERN=false -DLIBXML2_WITH_PYTHON=false -DLIBXML2_WITH_READER=false -DLIBXML2_WITH_REGEXPS=false -DLIBXML2_WITH_SCHEMAS=false -DLIBXML2_WITH_SCHEMATRON=false -DLIBXML2_WITH_TREE=false -DLIBXML2_WITH_VALID=false -DLIBXML2_WITH_WRITER=false -DLIBXML2_WITH_XINCLUDE=false -DLIBXML2_WITH_XPATH=false -DLIBXML2_WITH_XPTR=false
$MAKE install
